'use strict';

// tests for userFavEvent
// Generated by serverless-mocha-plugin

const mochaPlugin = require('serverless-mocha-plugin');
const expect = mochaPlugin.chai.expect;
const AWSMock = require('aws-sdk-mock');
let wrapped = mochaPlugin.getWrapper('userFavEvent', '/handlers/user.js', 'favouriteEvent');
const { USERS_TABLE, EVENTS_TABLE } = require('../constants/tables');

const testEntry = {
  eventID: 'some event id',
  isFavourite: true
};

const userObject = {
  id: '6456456464',
  fname: 'insanetest',
  lname: 'dude',
  faculty: 'Science',
  email: 'test@test.com'
};

describe('userFavEvent', () => {

  before(() => {

    AWSMock.mock('DynamoDB.DocumentClient', 'get', (params, callback) => {

      if(params.TableName.includes(USERS_TABLE) && params.Key.id === 6456456464) {

        callback(null, { Item: userObject });

      }
      else if(params.TableName.includes(EVENTS_TABLE) && params.Key.id === 'some event id') {

        callback(null, { Item: { id: 'some event id', capac: 100 } });

      }
      else callback(null, { Item: null });

    });

    AWSMock.mock('DynamoDB.DocumentClient', 'update', function (params, callback) {

      callback(null, { Item: 'not null user' });

    });

  });

  after(() => {

    AWSMock.restore('DynamoDB.DocumentClient');

  });

  it('returns 406 when eventID not provided', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        isFavourite: true
      }),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(406);

  });

  it('returns 406 when isFavourite is not provided', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        eventID: 'some event id'
      }),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(406);

  });

  it ('returns 406 when isFavourite is not a boolean', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        eventID: 'some event id',
        isFavourite: 'not a boolean'
      }),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(406);

  });

  it ('returns 404 when user does not exist', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        eventID: 'some event id',
        isFavourite: true
      }),
      pathParameters: {
        id: '12345678'
      }
    });
    expect(response.statusCode).to.equal(404);

  });

  it ('returns 404 when event does not exist', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        eventID: 'some event that does not exist',
        isFavourite: true
      }),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(404);

  });

  it('returns 200 when given valid data', async () => {

    const response = await wrapped.run({
      body: JSON.stringify(testEntry),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(200);

  });

  it('returns 200 when given valid data (false)', async () => {

    const response = await wrapped.run({
      body: JSON.stringify({
        ...testEntry,
        isFavourite: false
      }),
      pathParameters: {
        id: '6456456464'
      }
    });
    expect(response.statusCode).to.equal(200);

  });


});
